var timeValue = 500;
var currentElt = '';
var timerId = -1;

function displayResumeCard() {
  var idr = 'res'+currentElt;
  var idl = 'l'+currentElt;
  var celt = document.getElementById(idr);
  if (!celt) return false;
  document.getElementById(idr).style.display = '';
  document.getElementById(idl).style.display = 'none';
  return true;
}

function hideResumeCard(elt) {
  var idr = 'res'+elt;
  var idl = 'l'+elt;
  var celt = document.getElementById(idr);
  if (!celt) return false;
  document.getElementById(idr).style.display = 'none';
  document.getElementById(idl).style.display = '';
  return true;
}

function initTimer(elt) {
  if (currentElt == elt) return true;
  resetTimer();
  currentElt = elt;
  timerId = self.setTimeout("displayResumeCard()", timeValue);
  return true;
}

function resetTimer() {
  if (timerId!=-1) clearTimeout(timerId);
  timerId = -1;
  currentElt = '';
}
