script:
var oFCKeditor[aid] = new FCKeditor( '_[aid]' ) ;

oFCKeditor[aid].BasePath = 'fckeditor/';
oFCKeditor[aid].Config['FullPage'] = false ;
oFCKeditor[aid].Config['SkinPath'] =  window.location.pathname+'/fckeditor/editor/skins/silver/';
oFCKeditor[aid].Config['ToolbarStartExpanded'] =  [toolbarexpand];

oFCKeditor[aid].Config['ImageBrowserURL'] = '../../../?sole=Y&app=FDL&action=FCKIMAGE' ;
oFCKeditor[aid].Config['ImageUploadURL'] = '../../../?sole=Y&app=FDL&action=FCKUPLOAD' ;
oFCKeditor[aid].Config['ImageBrowserWindowWidth'] = '550px';
oFCKeditor[aid].Config['ImageBrowserWindowHeight'] = '400px';
oFCKeditor[aid].Config['LinkBrowser'] = false ;

//oFCKeditor[aid].Config['FontNames'] =  'serif;sans-serif;cursive;fantasy;monospace' ;
oFCKeditor[aid].ToolbarSet = '[toolbar]';
oFCKeditor[aid].Config["CustomConfigurationsPath"] = "../../FDL/Layout/fdl_fckeditor.js"  ;
oFCKeditor[aid].Height='[height]';
oFCKeditor[aid].Value='[Value]';

oFCKeditor[aid].Create();

NB_FCKEDITORS++;
var ita=document.getElementById('_[aid]');
if (ita) ita.name='[aname]';

FCKEDITORS.push('_[aid]');
function trackQS_[aid]() {
  var oEditor = FCKeditorAPI.GetInstance('_[aid]') ;

  if (oEditor && oEditor.EditorDocument) {   
    addEvent(oEditor.EditorDocument,"keydown",trackKeysQuickSave);  
    if (isNetscape) addEvent(oEditor.EditorDocument,"mouseover",fckforceedit_[aid]);    
  } else window.setTimeout( trackQS_[aid], 2400 ) ;
}

function fckforceedit_[aid]() {
  var oEditor = FCKeditorAPI.GetInstance('_[aid]') ;

  if (oEditor && oEditor.EditorDocument) {
    if (  oEditor.EditMode == FCK_EDITMODE_WYSIWYG ){
      oEditor.MakeEditable();
    }
  } 
}
function wstop_[aid]() { 
  var oEditor = FCKeditorAPI.GetInstance('_[aid]') ;

  if (oEditor && oEditor.EditorDocument && (oEditor.Status==FCK_STATUS_COMPLETE)) {  
    NB_FCKEDITORS--;    
    if (NB_FCKEDITORS<=0) window.stop();
  } else {    
    window.setTimeout( wstop_[aid], 1000 ) ;
  }

 }
addEvent(window,"load",trackQS_[aid]);
if (isNetscape) window.setTimeout( wstop_[aid], 3000+100*NB_FCKEDITORS ) ;
