script:
var mstart=[mstart];
var mend=[mend];
var mdelta=mend-mstart;
var tevents=new Array();
var zoom=1;
var zoomx=1;
var zoomy=1;
var maxw;
var maxh=300;;
var dh=17;
var bx=50;
var by;
var rw=50; // ressource column width
var oy=80; // margin offset in y
var eh=20; // offset event position
var xyby;

function placeEvt(divid,line,subline,x,w) {
  var dw=maxw*zoomx;
  var dih=dh*zoomy;
  var oimg= document.getElementById(divid);
  var ores= document.getElementById('res'+divid);
  if (oimg) {
    oimg.style.left=rw+bx+(x-mstart)*dw/mdelta;
    h=eh+by+(subline*dih);
    if (h>maxh) maxh=h;
    oimg.style.top=h;
    //oimg.width=(w)*dw/(mend-mstart);
    //oimg.height=dih;

    oimg.style.width=(w)*dw/mdelta;
    oimg.style.height=(dh-2)*zoomy;
    oimg.style.display='';;

    ores.style.top=h;
    ores.style.left=bx;
    ores.style.width=rw;
    ores.style.height=(dh-2)*zoomy;
    ores.style.display='';;
  }
}
function placeEvents() {
  xyby=getAnchorPosition('bgmilli');
  viewgyro(true);
  by=xyby.y+oy;
  bx=xyby.x;
  maxh=100;
  maxw=Math.max(getObjectWidth(document.body),400)-bx-rw;
  for (i=0;i0) {
    ocdday.removeChild(ocdday.childNodes[0]);
  }
  pXMonth=parseInt(rw+bx); // precedent 
  pXYear=pXMonth;
  for (var i=0;i 10) { // mini 10 pixel
	if (nWeek==0) {
	  onweek=odday.cloneNode(true);
	  onweek.id='dweek0'+i;

	  onweek.innerHTML=jdToWeekNUmber(mstart+i-1);
	  onweek.style.width=parseInt(dx*zoomx);
	  onweek.style.height=20;
	  onweek.style.top=by-40;
	  onweek.style.left=parseInt(rw+bx);
	  if ((nWeek % 2) == 1) onweek.style.backgroundColor='orange';
	  else onweek.style.backgroundColor='green';
	  ocdday.appendChild(onweek);	
	}
	onweek=odday.cloneNode(true);
	onweek.id='dweek'+i;
	onweek.innerHTML=jdToWeekNUmber(mstart+i);
	onweek.title=jd_to_cal(mstart+i);
	onweek.style.width=Math.min(parseInt(dw*zoomx*7),(maxw-dx)*zoomx);
	onweek.style.height=20;
	onweek.style.top=by-40;
	onweek.style.left=parseInt(rw+bx+(dx*zoomx));
	if ((nWeek % 2) == 0) onweek.style.backgroundColor='orange';
	else onweek.style.backgroundColor='green';
	ocdday.appendChild(onweek);
	nWeek++;
      }
    }
    nDay=parseInt(jd_to_cal(mstart+i,'d'));

    if (nDay == 1) {
      nMonth=(jd_to_cal(mstart+i,'M'));

      if ((dw*zoomx*30) > 10) { // mini 10 pixel
	// begin month
	onmonth=odday.cloneNode(true);
	onmonth.id='dmonth0'+i;

	onmonth.innerHTML=month[(nMonth+10)%12];
	onmonth.style.width=parseInt(rw+bx+(dx*zoomx))-pXMonth;
	onmonth.style.height=20;
	onmonth.style.top=by-60;
	onmonth.style.left=pXMonth;
	if ((nMonth % 2) == 1) onmonth.style.backgroundColor='pink';
	else onmonth.style.backgroundColor='salmon';
	ocdday.appendChild(onmonth);	
	pXMonth=parseInt(rw+bx+(dx*zoomx));
      }
      if (nMonth == 1) {
	// happy new year
	onyear=odday.cloneNode(true);
	onyear.id='dyear'+i;

	onyear.innerHTML=jd_to_cal(mstart+i-1,'Y');
	onyear.style.width=parseInt(rw+bx+(dx*zoomx))-pXYear;
	onyear.style.height=20;
	onyear.style.top=by-80;
	onyear.style.left=pXYear; 
	onyear.style.backgroundColor='yellow';
	ocdday.appendChild(onyear);	
	pXYear=parseInt(rw+bx+(dx*zoomx));
      }
    }
    if ((dw*zoomx) > 10) {
      onday=odday.cloneNode(true);
      onday.id='dday'+i;
      onday.title=weekDay(mstart+i)+jd_to_cal(mstart+i);
      onday.innerHTML=jd_to_cal(mstart+i,'d');
      onday.style.top=by-20;

      onday.style.width=parseInt(dw*zoomx);
      onday.style.height=20
      onday.style.left=parseInt(rw+bx+(dx*zoomx));
      if ((i % 2) == 0) onday.style.backgroundColor='lightyellow';
      else onday.style.backgroundColor='lightblue';
      ocdday.appendChild(onday);
      if ((dw*zoomx) > 100) {
	// include hours
	onhour=odhour.cloneNode(true);
	onhour.id='dhour'+i;
	onhour.title=weekDay(mstart+i)+jd_to_cal(mstart+i);

	onhour.style.top=by;

	onhour.style.width=parseInt(dw*zoomx);
	onhour.style.height=20
	  onhour.style.left=parseInt(rw+bx+(dx*zoomx));
	if ((i % 2) == 0) onhour.style.backgroundColor='lightyellow';
	else onhour.style.backgroundColor='lightblue';
	ocdday.appendChild(onhour);
      }
    }
  }

  // last month
  if ((dw*zoomx*30) > 10) { // mini 10 pixel
    nMonth=parseInt(jd_to_cal(mstart+i-0.01,'M'));
    // begin month
    onmonth=odday.cloneNode(true);
    onmonth.id='dmonth'+i;

    onmonth.innerHTML=month[(nMonth+11)%12];
    onmonth.style.width=parseInt(rw+bx+((dw+dx)*zoomx))-pXMonth;
    onmonth.style.height=20;
    onmonth.style.top=by-60;
    onmonth.style.left=pXMonth;
    if ((nMonth % 2) == 1) onmonth.style.backgroundColor='pink';
    else onmonth.style.backgroundColor='salmon';
    ocdday.appendChild(onmonth);	
    pXMonth=parseInt(rw+bx+(dx*zoomx));
  }
  // last year
	// happy new year
	onyear=odday.cloneNode(true);
	onyear.id='dyear'+i;

	onyear.innerHTML=jd_to_cal(mstart+i,'Y');
	onyear.style.width=parseInt(rw+bx+((dw+dx)*zoomx))-pXYear;
	onyear.style.height=20;
	onyear.style.top=by-80;
	onyear.style.left=pXYear; 
	onyear.style.backgroundColor='yellow';
	ocdday.appendChild(onyear);	
	pXYear=parseInt(rw+bx+(dx*zoomx));

}

function viewgyro(v) {
  var wimgo = document.getElementById('WGYRO');
  if (wimgo) {
    if (v)  {
      wimgo.style.visibility='visible';
      wimgo.style.zIndex=200;

      //CenterDiv(wimgo.id);
    } else {
       wimgo.style.visibility='hidden';
    }

  }
}
addEvent(window,"load",placeEvents);
